import { Component } from '@angular/core';

@Component({
  selector: 'app-welcome',
  templateUrl: './welcome.component.html',
  styleUrls: ['./welcome.component.css']
})
export class WelcomeComponent {

}
<div class="welcome-container">
  <h1>Welcome to Pokémon Collection Tracker</h1>
  <p>Manage your Pokémon card collection, track ownership, and check probabilities.</p>
  <div class="buttons">
    <button routerLink="/collection">View Collection</button>
    <button routerLink="/stats">View Stats</button>
  </div>
</div>
