import { Component, OnInit } from '@angular/core';
import { CollectionService } from '../collection.service';
import { Card } from '../models/card.model';

@Component({
  selector: 'app-collection',
  templateUrl: './collection.component.html',
  styleUrls: ['./collection.component.css']
})
export class CollectionComponent implements OnInit {
  collection: Card[] = [];

  constructor(private collectionService: CollectionService) {}

  ngOnInit(): void {
    this.loadCollection();
  }

  loadCollection(): void {
    this.collectionService.getCollection().subscribe((data: Card[]) => {
      this.collection = data.map(card => ({
        ...card,
        PositionProbabilities: card.PositionProbabilities || { "1-3": 0, "4": 0, "5": 0 }
      }));
    });
  }

  toggleOwnership(card: Card): void {
    card.Owned = !card.Owned;
    this.collectionService.updateCollection(this.collection).subscribe();
  }
}
<div
  class="card-item"
  [ngClass]="{
    'pikachu-card': card.Pack === 'Pikachu',
    'charizard-card': card.Pack === 'Charizard',
    'mewtwo-card': card.Pack === 'Mewtwo',
    'light-background': card.Pack === 'Pikachu' || card.Pack === 'OtherLightBackgroundPack'
  }"
>
  <div class="card-details">
    <p>
      <strong>ID:</strong> {{ card.ID }} -
      <strong>Name:</strong> {{ card.Name }} - Pack: {{ card.Pack }} - Rarity:
      {{ card.Rarity }}
    </p>
    <p>Chance in positions:</p>
    <ul>
      <li>First-Third: {{ card.PositionProbabilities['1-3'] | number: '1.2-2' }}%</li>
      <li>Fourth: {{ card.PositionProbabilities['4'] | number: '1.2-2' }}%</li>
      <li>Fifth: {{ card.PositionProbabilities['5'] | number: '1.2-2' }}%</li>
    </ul>
  </div>
  <button class="owned-button" (click)="toggleOwnership(card)">
    {{ card.Owned ? 'Owned' : 'Not Owned' }}
  </button>
</div>
